import * as React from 'react';
import OtpService from '../services/OtpService';
import { CountdownCircleTimer } from 'react-countdown-circle-timer';

export default class MainComponent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            otpService: new OtpService(),
            userID: null,
            showTimer: false,
            generatedCode: null
        };
    }

    generateCode = () => {
        this.state.otpService.get(this.state.userID).then((data) => {
            this.setState({
                showTimer: true,
                generatedCode: data
            });
        });
    }

    dataChanged = (event) => {
        this.setState({
            userID: event.target.value
        });
    }

    renderComponent = () => {
        let timer;
        if (this.state.showTimer == true) {
            timer = <div>
                <CountdownCircleTimer
                    onComplete={() => {
                        this.setState({
                            showTimer: false,
                            data: null,
                            userID: null
                        });
                    }}
                    isPlaying
                    duration={30}
                    colors={[['#A30000']]}
                >
                    Current Valid Code: {this.state.generatedCode}

                </CountdownCircleTimer>
            </div>;
        }
        else {
            timer = <div></div>;
        }

        return (
            <div>
                <div>
                    <input type="text" onChange={this.dataChanged} name="userID" />
                    <button onClick={this.generateCode}>GenerateCode</button>
                </div>
                {timer}
            </div>
        )
    }

    render() {

        return (
            <div>
                {this.renderComponent()}
            </div>
        )
    }
}
